import { useToast } from "@/hooks/use-toast"
import {
  Toast,
  ToastClose,
  ToastDescription,
  ToastProvider,
  ToastTitle,
  ToastViewport,
} from "@/components/ui/toast"

export function Toaster() {
  const { toasts } = useToast()

  return (
    <ToastProvider>
      {toasts.map(function ({ id, title, description, action, ...props }) {
        return (
          <Toast key={id} {...props}>
            <div className="grid gap-1">
              {title && <ToastTitle>{title}</ToastTitle>}
              {description && (
                <ToastDescription>{description}</ToastDescription>
              )}
            </div>
            {action}
            <ToastClose />
          </Toast>
        )
      })}
      <ToastViewport />
    </ToastProvider>
  )
}

client/src/pages/checkout.tsx.content:
import { useState } from "react";
import { Link, useLocation } from "wouter";
import { motion } from "framer-motion";
import { CreditCard, Wallet, Smartphone, ChevronLeft, Check } from "lucide-react";
import { useCart } from "@/lib/cart-context";

type PaymentMethod = "card" | "paypal" | "upi";

export default function Checkout() {
  const { items, totalPrice, clearCart, getProduct } = useCart();
  const [, setLocation] = useLocation();
  const [paymentMethod, setPaymentMethod] = useState<PaymentMethod>("card");
  const [isProcessing, setIsProcessing] = useState(false);
  const [orderComplete, setOrderComplete] = useState(false);

  const shippingCost = totalPrice >= 200 ? 0 : 15;
  const orderTotal = totalPrice + shippingCost;

  const handlePlaceOrder = () => {
    setIsProcessing(true);
    setTimeout(() => {
      setIsProcessing(false);
      setOrderComplete(true);
      clearCart();
    }, 2000);
  };

  if (orderComplete) {
    return (
      <motion.main
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        className="min-h-screen bg-white pt-20 pb-16 px-4"
      >
        <div className="max-w-md mx-auto text-center">
          <motion.div
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ type: "spring", damping: 15 }}
            className="w-20 h-20 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-6"
          >
            <Check className="w-10 h-10 text-green-600" />
          </motion.div>
          <h1 className="font-heading text-3xl font-semibold text-black mb-3">
            Order Confirmed!
          </h1>
          <p className="text-neutral-600 mb-8">
            Thank you for your purchase. Your order will be shipped within 2-3 business days.
          </p>
          <Link href="/">
            <button className="px-8 py-3 bg-black text-white font-medium rounded-full hover:bg-neutral-800 transition-colors">
              Continue Shopping
            </button>
          </Link>
        </div>
      </motion.main>
    );
  }

  if (items.length === 0) {
    return (
      <motion.main
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        className="min-h-screen bg-white pt-20 pb-16 px-4"
      >
        <div className="max-w-md mx-auto text-center">
          <h1 className="font-heading text-2xl font-semibold text-black mb-3">
            Your bag is empty
          </h1>
          <p className="text-neutral-600 mb-6">
            Add some items to your bag before checking out.
          </p>
          <Link href="/">
            <button className="px-8 py-3 bg-black text-white font-medium rounded-full hover:bg-neutral-800 transition-colors">
              Start Shopping
            </button>
          </Link>
        </div>
      </motion.main>
    );
  }

  return (
    <motion.main
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.3 }}
      className="min-h-screen bg-neutral-50 pt-16"
    >
      <div className="max-w-[1000px] mx-auto px-4 md:px-6 py-8">
        <button
          onClick={() => setLocation("/")}
          className="flex items-center gap-2 text-neutral-600 hover:text-black transition-colors mb-6"
        >
          <ChevronLeft className="w-4 h-4" />
          <span className="text-sm font-medium">Continue Shopping</span>
        </button>

        <h1 className="font-heading text-2xl md:text-3xl font-semibold text-black mb-8">
          Checkout
        </h1>

        <div className="grid grid-cols-1 lg:grid-cols-5 gap-8">
          <div className="lg:col-span-3 space-y-6">
            <div className="bg-white rounded-2xl p-6">
              <h2 className="font-semibold text-black mb-4">Payment Method</h2>
              <div className="space-y-3">
                <button
                  onClick={() => setPaymentMethod("card")}
                  className={`w-full flex items-center gap-4 p-4 rounded-xl border-2 transition-colors ${
                    paymentMethod === "card"
                      ? "border-black bg-neutral-50"
                      : "border-neutral-200 hover:border-neutral-300"
                  }`}
                >
                  <div className={`w-10 h-10 rounded-full flex items-center justify-center ${
                    paymentMethod === "card" ? "bg-black" : "bg-neutral-100"
                  }`}>
                    <CreditCard className={`w-5 h-5 ${paymentMethod === "card" ? "text-white" : "text-neutral-600"}`} />
                  </div>
                  <div className="text-left">
                    <p className="font-medium text-black">Credit / Debit Card</p>
                    <p className="text-sm text-neutral-500">Visa, Mastercard, Amex</p>
                  </div>
                </button>

                <button
                  onClick={() => setPaymentMethod("paypal")}
                  className={`w-full flex items-center gap-4 p-4 rounded-xl border-2 transition-colors ${
                    paymentMethod === "paypal"
                      ? "border-black bg-neutral-50"
                      : "border-neutral-200 hover:border-neutral-300"
                  }`}
                >
                  <div className={`w-10 h-10 rounded-full flex items-center justify-center ${
                    paymentMethod === "paypal" ? "bg-black" : "bg-neutral-100"
                  }`}>
                    <Wallet className={`w-5 h-5 ${paymentMethod === "paypal" ? "text-white" : "text-neutral-600"}`} />
                  </div>
                  <div className="text-left">
                    <p className="font-medium text-black">PayPal</p>
                    <p className="text-sm text-neutral-500">Fast & secure payment</p>
                  </div>
                </button>

                <button
                  onClick={() => setPaymentMethod("upi")}
                  className={`w-full flex items-center gap-4 p-4 rounded-xl border-2 transition-colors ${
                    paymentMethod === "upi"
                      ? "border-black bg-neutral-50"
                      : "border-neutral-200 hover:border-neutral-300"
                  }`}
                >
                  <div className={`w-10 h-10 rounded-full flex items-center justify-center ${
                    paymentMethod === "upi" ? "bg-black" : "bg-neutral-100"
                  }`}>
                    <Smartphone className={`w-5 h-5 ${paymentMethod === "upi" ? "text-white" : "text-neutral-600"}`} />
                  </div>
                  <div className="text-left">
                    <p className="font-medium text-black">UPI</p>
                    <p className="text-sm text-neutral-500">GPay, PhonePe, Paytm</p>
                  </div>
                </button>
              </div>
            </div>

            {paymentMethod === "card" && (
              <motion.div
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                className="bg-white rounded-2xl p-6"
              >
                <h2 className="font-semibold text-black mb-4">Card Details</h2>
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-black mb-2">Card Number</label>
                    <input
                      type="text"
                      placeholder="1234 5678 9012 3456"
                      className="w-full px-4 py-3 rounded-xl border border-neutral-200 focus:outline-none focus:ring-2 focus:ring-black"
                    />
                  </div>
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-black mb-2">Expiry Date</label>
                      <input
                        type="text"
                        placeholder="MM/YY"
                        className="w-full px-4 py-3 rounded-xl border border-neutral-200 focus:outline-none focus:ring-2 focus:ring-black"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-black mb-2">CVV</label>
                      <input
                        type="text"
                        placeholder="123"
                        className="w-full px-4 py-3 rounded-xl border border-neutral-200 focus:outline-none focus:ring-2 focus:ring-black"
                      />
                    </div>
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-black mb-2">Name on Card</label>
                    <input
                      type="text"
                      placeholder="John Doe"
                      className="w-full px-4 py-3 rounded-xl border border-neutral-200 focus:outline-none focus:ring-2 focus:ring-black"
                    />
                  </div>
                </div>
              </motion.div>
            )}
          </div>

          <div className="lg:col-span-2">
            <div className="bg-white rounded-2xl p-6 sticky top-20">
              <h2 className="font-semibold text-black mb-4">Order Summary</h2>

              <div className="space-y-4 mb-6">
                {items.map((item) => {
                  const product = getProduct(item.productId);
                  if (!product) return null;
                  return (
                    <div key={`${item.productId}-${item.size}-${item.color}`} className="flex gap-4">
                      <div className="w-16 h-16 rounded-lg overflow-hidden bg-neutral-100 flex-shrink-0">
                        <img
                          src={product.image}
                          alt={product.name}
                          className="w-full h-full object-cover"
                        />
                      </div>
                      <div className="flex-1 min-w-0">
                        <p className="font-medium text-black text-sm truncate">{product.name}</p>
                        <p className="text-xs text-neutral-500">
                          {item.size && `Size: ${item.size}`}
                          {item.size && item.color && "  "}
                          {item.color && item.color}
                        </p>
                        <p className="text-sm text-neutral-600 mt-1">
                          Qty: {item.quantity}  ${product.price.toLocaleString()}
                        </p>
                      </div>
                    </div>
                  );
                })}
              </div>

              <div className="border-t border-neutral-100 pt-4 space-y-2">
                <div className="flex justify-between text-sm">
                  <span className="text-neutral-600">Subtotal</span>
                  <span className="text-black">${totalPrice.toLocaleString()}</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span className="text-neutral-600">Shipping</span>
                  <span className="text-black">
                    {shippingCost === 0 ? "Free" : `$${shippingCost}`}
                  </span>
                </div>
                <div className="flex justify-between font-semibold text-black pt-2 border-t border-neutral-100">
                  <span>Total</span>
                  <span>${orderTotal.toLocaleString()}</span>
                </div>
              </div>

              <button
                onClick={handlePlaceOrder}
                disabled={isProcessing}
                className="w-full mt-6 py-4 bg-black text-white font-medium rounded-full hover:bg-neutral-800 transition-colors disabled:bg-neutral-400 disabled:cursor-not-allowed"
              >
                {isProcessing ? "Processing..." : `Pay $${orderTotal.toLocaleString()}`}
              </button>

              <p className="text-xs text-neutral-500 text-center mt-4">
                Your payment information is encrypted and secure
              </p>
            </div>
          </div>
        </div>
      </div>
    </motion.main>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

script/build.ts.content:
import { build as esbuild } from "esbuild";
import { build as viteBuild } from "vite";
import { rm, readFile } from "fs/promises";

// server deps to bundle to reduce openat(2) syscalls
// which helps cold start times
const allowlist = [
  "@google/generative-ai",
  "axios",
  "connect-pg-simple",
  "cors",
  "date-fns",
  "drizzle-orm",
  "drizzle-zod",
  "express",
  "express-rate-limit",
  "express-session",
  "jsonwebtoken",
  "memorystore",
  "multer",
  "nanoid",
  "nodemailer",
  "openai",
  "passport",
  "passport-local",
  "pg",
  "stripe",
  "uuid",
  "ws",
  "xlsx",
  "zod",
  "zod-validation-error",
];

async function buildAll() {
  await rm("dist", { recursive: true, force: true });

  console.log("building client...");
  await viteBuild();

  console.log("building server...");
  const pkg = JSON.parse(await readFile("package.json", "utf-8"));
  const allDeps = [
    ...Object.keys(pkg.dependencies || {}),
    ...Object.keys(pkg.devDependencies || {}),
  ];
  const externals = allDeps.filter((dep) => !allowlist.includes(dep));

  await esbuild({
    entryPoints: ["server/index.ts"],
    platform: "node",
    bundle: true,
    format: "cjs",
    outfile: "dist/index.cjs",
    define: {
      "process.env.NODE_ENV": '"production"',
    },
    minify: true,
    external: externals,
    logLevel: "info",
  });
}

buildAll().catch((err) => {
  console.error(err);
  process.exit(1);
});

