import { motion } from "framer-motion";
import { InfiniteCarousel } from "@/components/infinite-carousel";
import { CategoryScroll } from "@/components/category-scroll";
import { NewArrivals } from "@/components/new-arrivals";
import { TrendingSection } from "@/components/trending-section";
import { TrustBadges } from "@/components/trust-badges";
import { Footer } from "@/components/footer";

export default function Home() {
  return (
    <motion.main
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.3 }}
      className="bg-white"
      data-testid="page-home"
    >
      <InfiniteCarousel />
      <CategoryScroll />
      <NewArrivals />
      <TrendingSection />

      <section className="py-16 px-4 md:px-6 bg-white">
        <div className="max-w-[800px] mx-auto text-center">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.5 }}
          >
            <p className="text-xs tracking-widest text-neutral-500 uppercase mb-4">
              Our Philosophy
            </p>
            <h2 className="font-heading text-2xl md:text-3xl font-medium text-black leading-relaxed">
              At ZENTRIX, we believe that true luxury is found in the{" "}
              <span className="text-neutral-500">perfect balance</span> of innovation and timeless design.
            </h2>
          </motion.div>
        </div>
      </section>

      <section className="py-12 px-4 md:px-6 bg-neutral-50">
        <div className="max-w-[1200px] mx-auto">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {[
              {
                title: "Craftsmanship",
                description: "Every piece is meticulously crafted by master artisans using only the finest materials.",
              },
              {
                title: "Sustainability",
                description: "We're committed to ethical sourcing and sustainable practices throughout our supply chain.",
              },
              {
                title: "Innovation",
                description: "Pioneering new techniques while honoring traditional methods of excellence.",
              },
            ].map((feature, index) => (
              <motion.div
                key={feature.title}
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ delay: index * 0.1, duration: 0.4 }}
                className="text-center p-6"
              >
                <h3 className="text-base font-semibold text-black mb-2">
                  {feature.title}
                </h3>
                <p className="text-sm text-neutral-600 leading-relaxed">
                  {feature.description}
                </p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      <TrustBadges />
      <Footer />
    </motion.main>
  );
}

