import { useState, useEffect, useCallback } from "react";
import { Link } from "wouter";
import { motion, AnimatePresence } from "framer-motion";
import { ChevronLeft, ChevronRight } from "lucide-react";
import { heroSlides } from "@/lib/data";

export function HeroCarousel() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isAutoPlaying, setIsAutoPlaying] = useState(true);

  const nextSlide = useCallback(() => {
    setCurrentIndex((prev) => (prev + 1) % heroSlides.length);
  }, []);

  const prevSlide = useCallback(() => {
    setCurrentIndex((prev) => (prev - 1 + heroSlides.length) % heroSlides.length);
  }, []);

  useEffect(() => {
    if (!isAutoPlaying) return;
    const interval = setInterval(nextSlide, 5000);
    return () => clearInterval(interval);
  }, [isAutoPlaying, nextSlide]);

  return (
    <section className="pt-16 pb-6 px-4 md:px-6 bg-white">
      <div className="max-w-[1200px] mx-auto">
        <div className="md:hidden">
          <div
            className="relative overflow-hidden rounded-2xl aspect-video"
            onMouseEnter={() => setIsAutoPlaying(false)}
            onMouseLeave={() => setIsAutoPlaying(true)}
          >
            <AnimatePresence mode="wait">
              <motion.div
                key={currentIndex}
                initial={{ opacity: 0, x: 100 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -100 }}
                transition={{ duration: 0.4 }}
                className="relative w-full h-full"
              >
                <Link href={heroSlides[currentIndex].link}>
                  <div className="relative w-full aspect-video cursor-pointer">
                    <img
                      src={heroSlides[currentIndex].image}
                      alt={heroSlides[currentIndex].title}
                      className="w-full h-full object-cover rounded-2xl"
                    />
                    <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-black/20 to-transparent rounded-2xl" />
                    <div className="absolute bottom-4 left-4 right-4">
                      <p className="text-xs text-white/80 uppercase tracking-wider mb-1">
                        {heroSlides[currentIndex].subtitle}
                      </p>
                      <h2 className="text-xl font-semibold text-white mb-2">
                        {heroSlides[currentIndex].title}
                      </h2>
                      <span className="text-sm text-white/90 hover:text-white">
                        {heroSlides[currentIndex].cta} 
                      </span>
                    </div>
                  </div>
                </Link>
              </motion.div>
            </AnimatePresence>

            <button
              onClick={prevSlide}
              className="absolute left-2 top-1/2 -translate-y-1/2 w-8 h-8 bg-white/80 backdrop-blur-sm rounded-full flex items-center justify-center hover:bg-white transition-colors"
            >
              <ChevronLeft className="w-4 h-4 text-black" />
            </button>
            <button
              onClick={nextSlide}
              className="absolute right-2 top-1/2 -translate-y-1/2 w-8 h-8 bg-white/80 backdrop-blur-sm rounded-full flex items-center justify-center hover:bg-white transition-colors"
            >
              <ChevronRight className="w-4 h-4 text-black" />
            </button>

            <div className="absolute bottom-2 left-1/2 -translate-x-1/2 flex gap-1.5">
              {heroSlides.map((_, idx) => (
                <button
                  key={idx}
                  onClick={() => setCurrentIndex(idx)}
                  className={`w-1.5 h-1.5 rounded-full transition-colors ${
                    idx === currentIndex ? "bg-white" : "bg-white/50"
                  }`}
                />
              ))}
            </div>
          </div>
        </div>

        <div className="hidden md:grid md:grid-cols-2 gap-4">
          {heroSlides.slice(0, 2).map((slide, idx) => (
            <Link key={slide.id} href={slide.link}>
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: idx * 0.1 }}
                className="relative aspect-video rounded-2xl overflow-hidden cursor-pointer group"
              >
                <img
                  src={slide.image}
                  alt={slide.title}
                  className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-500"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-black/20 to-transparent" />
                <div className="absolute bottom-6 left-6 right-6">
                  <p className="text-xs text-white/80 uppercase tracking-wider mb-1">
                    {slide.subtitle}
                  </p>
                  <h2 className="text-2xl font-semibold text-white mb-2">
                    {slide.title}
                  </h2>
                  <span className="text-sm text-white/90 group-hover:text-white transition-colors">
                    {slide.cta} 
                  </span>
                </div>
              </motion.div>
            </Link>
          ))}
        </div>
      </div>
    </section>
  );
}

client/src/components/product-card.tsx.content:
import { useState } from "react";
import { Link } from "wouter";
import { motion } from "framer-motion";
import { ShoppingBag } from "lucide-react";
import { useCart } from "@/lib/cart-context";
import { CheckoutModal } from "@/components/checkout-modal";
import type { Product } from "@shared/schema";

interface ProductCardProps {
  product: Product;
  index?: number;
  onBuyNow?: (product: Product) => void;
}

export function ProductCard({ product, index = 0, onBuyNow }: ProductCardProps) {
  const { addItem } = useCart();
  const [checkoutOpen, setCheckoutOpen] = useState(false);

  const handleAddToBag = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    addItem(product, 1, product.sizes?.[0], product.colors?.[0]);
  };

  const handleBuyNow = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    addItem(product, 1, product.sizes?.[0], product.colors?.[0]);
    if (onBuyNow) {
      onBuyNow(product);
    } else {
      setCheckoutOpen(true);
    }
  };

  return (
    <>
      <motion.div
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: index * 0.03, duration: 0.2 }}
        className="group bg-white rounded-2xl overflow-hidden"
        data-testid={`card-product-${product.id}`}
      >
        <Link href={`/product/${product.id}`}>
          <div className="relative aspect-square overflow-hidden bg-[#F5F5F7] rounded-2xl">
            <img
              src={product.image}
              alt={product.name}
              className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-500"
            />
            {product.isNew && (
              <span className="absolute top-3 left-3 px-2.5 py-1 bg-black text-white text-[10px] font-medium rounded-full tracking-wide">
                NEW
              </span>
            )}
          </div>
        </Link>

        <div className="pt-3 pb-1">
          <Link href={`/product/${product.id}`}>
            <h3 className="text-sm font-medium text-black mb-0.5 truncate hover:text-blue-600 cursor-pointer transition-colors">
              {product.name}
            </h3>
          </Link>
          <p className="text-sm text-neutral-500 mb-3">
            ${product.price.toLocaleString()}
          </p>

          <div className="flex gap-2">
            <button
              onClick={handleAddToBag}
              className="flex-1 flex items-center justify-center gap-1.5 px-3 py-2.5 border border-neutral-300 hover:border-neutral-400 rounded-full text-xs font-medium text-black transition-colors"
              data-testid={`button-add-to-bag-${product.id}`}
            >
              <ShoppingBag className="w-3.5 h-3.5" />
              <span>Add to Bag</span>
            </button>
            <button
              onClick={handleBuyNow}
              className="flex-1 px-3 py-2.5 bg-blue-600 hover:bg-blue-700 rounded-full text-xs font-medium text-white transition-colors"
              data-testid={`button-buy-now-${product.id}`}
            >
              Buy Now
            </button>
          </div>
        </div>
      </motion.div>

      {!onBuyNow && (
        <CheckoutModal isOpen={checkoutOpen} onClose={() => setCheckoutOpen(false)} />
      )}
    </>
  );
}

